import { createStore } from 'vuex'

const store=createStore({
  state:{
    menu:[]
  },
  getters: {
    // 对数据进行过滤
  },
  mutations: {
    initMenus(menus,state){
      state.menu=menus;
    }
  },
  actions: {
  },
  modules: {
  }
})

export default store;
