import instance from "@/util/axiosUtil";

const permissions_request={
    /*
    获取操作权限总条数
     */
    getPermissionCount(){
        return instance({
            method:"get",
            url:"/getPermissionCount"
        })
    },
    // 获取所有权限
    getAllPermission(page,size) {
        return instance({
            method: 'get',
            url: '/getAllPermission',
            params:{
                page:page,
                size:size
            }
        })
    },
}

export default permissions_request;
