import { createStore } from 'vuex'

const store=createStore({
  state:{
    menu:[],
    Tags: [],
  },
  getters: {
    // 对数据进行过滤
  },
  mutations: {
    initMenu(state,menuData){
      state.menu=menuData;
    },
    addTag(state,tagName){
      state.Tags.push(tagName)
    }
  },
  actions: {
  },
  modules: {
  }
})

export default store;
