import {ElMessage, ElMessageBox} from "element-plus";

export function update_Menu_Role(data){
    ElMessageBox.confirm(
        '此操作将会修改【'+data.nameZh+'】的菜单权限,是否继续',
        {
            confirmButtonText: '确定',
            cancelButtonText: '取消',
            type: 'warning',
        }
    )
        .then(() => {
            ElMessage({
                type: 'info',
                message: '修改成功',
            })
        })
        .catch(() => {
            ElMessage({
                type: 'info',
                message: '操作取消',
            })
        })
}
