import {ElMessage, ElMessageBox} from "element-plus";
import menu_request from "@/api/menu";

export function update_Menu_Role(data,checkedNodes){
    ElMessageBox.confirm(
        '此操作将会修改【'+data.nameZh+'】的菜单权限,是否继续',
        {
            confirmButtonText: '确定',
            cancelButtonText: '取消',
            type: 'warning',
        }
    )
        .then(() => {
            // 将数组转为字符串
            let nodes=checkedNodes.join(',');
            menu_request.updateRoleMenuId(nodes).then(resp=>{
                console.log(resp.data);
            })
            ElMessage({
                type: 'info',
                message: '修改成功',
            })
        })
        .catch(() => {
            ElMessage({
                type: 'info',
                message: '操作取消',
            })
        })
}
