import axios from "axios";
import router from "../router";
import {ElMessage} from 'element-plus';
import {getCookie} from "@/util/cookieUtil";

const errorHandle = (status) => {
    switch (status) {
        case 400:
            ElMessage.error("语义有误");
            break;
        case 401:
            ElMessage.error("请进行登录授权");
            break;
        case 403:
            break;
        case 404:
            break;
        case 500:
            ElMessage.error("服务器遇到意外");
            break;
        case 502:
            ElMessage.error("服务器无响应");
            break;
        default:
            break;
    }
}
// 创建一个axios的实例
const instance = axios.create({
    // 网络请求的公共配置信息
    baseURL: '/api',
    timeout: 50000,
    withCredentials: true,
    headers: {'Content-Type': 'application/json'}
})
let isFirstRequest = true; // 添加一个标志变量用于标识是否为第一个请求

instance.interceptors.request.use(
    config => {
        if (sessionStorage.getItem("user") !== null) {
            let user = JSON.parse(sessionStorage.getItem("user"));
            let token = getCookie(user.username + 'token');
            if (token === null) {
                if (isFirstRequest) {
                    isFirstRequest = false;
                    ElMessage.error("会话过期，请重新登录");
                    router.push('/');
                    sessionStorage.removeItem("user");
                } else {
                    return Promise.reject(new Error("Session expired")); // 拒绝后面两个请求的执行
                }
            } else {
                config.headers["Authorization"] = token;
            }
        }
        return config;
    },
    error => {
        errorHandle(error.response.status);
        return Promise.reject(error);
    }
);


// 响应
instance.interceptors.response.use(
    success => {
        return success;
    },
    error => {
        return Promise.reject(error);
    }
)

// 导出网络实例
export default instance;
